// proceso1.c#include <stdio.h>#include <stdlib.h>#include <string.h>#include <sys/types.h>#include <sys/ipc.h>#include <sys/shm.h>#include "common.h"int main() {    int shmid;    datos *zona_comun;    // Crear/obtener zona de memoria compartida    shmid = shmget(CLAVE_SHM, sizeof(datos), IPC_CREAT | 0666);    if (shmid == -1) {        perror("Error en shmget");        exit(EXIT_FAILURE);    }    // Conectar a la memoria compartida    zona_comun = (datos *) shmat(shmid, NULL, 0);    if (zona_comun == (void *) -1) {        perror("Error en shmat");        exit(EXIT_FAILURE);    }    zona_comun->turno = 1; // Proceso 1 empieza primero    int intento = 0;    while (1) {        // Espera activa hasta que sea el turno del proceso 1        while (zona_comun->turno == 2) {}        // Es mi turno; realizo un intento        zona_comun->intento = intento;        printf("Proceso 1: Intento con el número %d\n", intento);        // Cedo el turno al proceso 2        zona_comun->turno = 2;        // Espera activa para leer la respuesta        while (zona_comun->turno == 1) {}        // Leer la respuesta del proceso 2        if (strcmp(zona_comun->respuesta, "acertado") == 0) {            printf("Proceso 1: ¡Adiviné el número %d! Finalizando...\n", intento);            break; // Termina el ciclo al adivinar correctamente        } else if (strcmp(zona_comun->respuesta, "mayor") == 0) {            printf("Proceso 1: El número es mayor que %d\n", intento);        } else if (strcmp(zona_comun->respuesta, "menor") == 0) {            printf("Proceso 1: El número es menor que %d\n", intento);        }        // Incremento el intento en 1 para el siguiente turno        intento++;    }    // Desconectar la memoria compartida y finalizar    shmdt(zona_comun);    return 0;}